// frontend/contact-manager-app/src/App.js

import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css"; // Если у вас есть стили

function App() {
  const [contacts, setContacts] = useState([]);
  const [searchTerm, setSearchTerm] = useState(""); // Состояние для поиска
  const [name, setName] = useState("");
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchContacts();
  }, []);

  const fetchContacts = async () => {
    const response = await axios.get("http://localhost:5000/contacts");
    setContacts(response.data);
  };

  const addContact = async () => {
    if (!name) return;
    if (editId) {
      await axios.put(`http://localhost:5000/contacts/${editId}`, { name });
      setEditId(null);
    } else {
      const response = await axios.post("http://localhost:5000/contacts", {
        name,
      });
      setContacts([...contacts, response.data]);
    }
    setName("");
    fetchContacts(); // Обновляем список контактов
  };

  const editContact = (contact) => {
    setEditId(contact.id);
    setName(contact.name);
  };

  const deleteContact = async (id) => {
    await axios.delete(`http://localhost:5000/contacts/${id}`);
    fetchContacts(); // Обновляем список контактов
  };

  // Функция для фильтрации контактов по имени
  const filteredContacts = contacts.filter((contact) =>
    contact.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <h1>Список контактов</h1>
      <input
        type="text"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        placeholder="Поиск контакта"
      />
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Имя контакта"
      />
      <button onClick={addContact}>
        {editId ? "Обновить контакт" : "Добавить контакт"}
      </button>
      <ul>
        {filteredContacts.map((contact) => (
          <li key={contact.id}>
            {contact.name}
            <button onClick={() => editContact(contact)}>Редактировать</button>
            <button onClick={() => deleteContact(contact.id)}>Удалить</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
